<template>
	<view id="suggest">
		<!-- 用户信息部分 -->
		<view class="msg">
			<text class="left">反馈类型:</text>
			<view class="uni-box" @click="showAction">
				{{val}}
				<view class="uni-list" v-show="isShow">
					<view class="item" @click="chageAction(item)"
					v-for="(item,index) in itemList" 
					:key="index">{{item}}</view>
				</view>
			</view>
		</view>
		
		<!-- 用户描述 -->
		<view class="desc">
			<view class="top">详情描述</view>
			<textarea class="con" placeholder="在这里输入您的描述......" v-model="desc"></textarea>
		</view>
		
		<!-- 提交按钮 -->
		<button type="default" class="commit" @click="commitAction">提交</button>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				isShow:false,
				val:"",
				itemList:["酒店问题","平台问题","其他问题"],
				desc:""
			};
		},
	   methods:{
		   commitAction(){
			   let val = this.dataList[0].val;
			   if(!(val && this.desc)){
				   uni.showToast({
				   	icon:"none",
					title:"请输入完整后在进行提交！"
				   });
				   return;
			   }
			   uni.showToast({
			   	title:"提交成功，感谢您提出的建议！"
			   });
		   },
		   chageAction(val){
			   this.val = val;
			   setTimeout(()=>{this.isShow = false;},0);
		   },
		   showAction(){
			   this.isShow = true;
		   }
	   },
	   onLoad(){
		   this.val = this.itemList[0];
	   }
	}
</script>

